import React, { useState } from "react";

function Form(props) {
  const initialUserInput = {
    "current-savings": 10000,
    "yearly-contribution": 1200,
    "expected-return": 7,
    duration: 10,
  };
  const [userInput, setUserInput] = useState(initialUserInput);

  function submitHanlder(e) {
    e.preventDefault();
    console.log("submit yayaya");
  }
  function resetHandler(e) {
    e.preventDefault();
    console.log("reset handler yayaya");
  }
  function changeHandler(event) {
    // console.log(event.target.value);
    // console.log(event.target.id);
    setUserInput((prevInput) => {
      return {
        ...prevInput,
        [event.target.id]: event.target.value,
      };
    });
  }
  return (
    <form className="form" onSubmit={submitHanlder}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input type="number" id="current-savings" onChange={changeHandler} />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            onChange={changeHandler}
            // alternative way to send multiple parameters to method -->
            // onChange={(e) => changeHandler('blahblah', e.target.value)}
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input type="number" id="expected-return" onChange={changeHandler} />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input type="number" id="duration" onChange={changeHandler} />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt" onClick={resetHandler}>
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
}

export default Form;
