import React, { useState } from "react";

function Form(props) {
  function submitHanlder(e) {
    e.preventDefault();
    console.log("submit yayaya");
  }
  function resetHandler(e) {
    e.preventDefault();
    console.log("reset handler yayaya");
  }
  function changeHandler(event, name) {
    console.log(event.target.value);
    console.log(event.target.id);
    // console.log(value.target.name);
  }
  return (
    <form className="form" onSubmit={submitHanlder}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="current-savings"
            onChange={changeHandler(e, "yoyo")}
          />
          // only executes when input value changes // why would I do this
          opposed to just calling the function?
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            onChange={(event) =>
              changeHandler("yearly-savings", event.target.value)
            }
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expected-return"
            onChange={(event) =>
              changeHandler("expected-interest", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            onChange={(event) =>
              changeHandler("investment-duration", event.target.value)
            }
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt" onClick={resetHandler}>
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
}

export default Form;
